# Specify the path to the ChromeDriver
PATH = "C:\Program Files (x86)\chromedriver.exe"
driver = webdriver.Chrome(PATH)

# Function to extract text from a page
def extract_text(url):
    driver.get(url)
    try:
        # Wait for the page to load and the element to be present
        WebDriverWait(driver, 10).until(
            EC.presence_of_element_located((By.XPATH, "//div[contains(@class, 'box-content has-text')]/p"))
        )
        
        # Find the <p> element within the <div> with class 'box-content has-text'
        text_element = driver.find_element(By.XPATH, "//div[contains(@class, 'box-content has-text')]/p")
        text = text_element.text
        return text
    except Exception as e:
        print(f"An error occurred while scraping {url}: {e}")
        return None

# Loop through the URLs and extract text
for url in urls:
    text = extract_text(url)
    if text:
        print(f"Text from {url}: {text}")

# Close the WebDriver
driver.quit()